		<script>
			//var uploadUrl = '/fckeditor/editor/filemanager/connectors/php/upload.php';
			var uploadUrl = 'http://localhost/forums/admin.php?mod=media&opt=inline';


			var config = {
				customConfig : '',
				toolbarCanCollapse : false,
				colorButton_enableMore : false,
				toolbar :
				[
					{ name: 'document',    items : [ 'Source' ] },
					{ name: 'clipboard',   items : [ 'Cut','Copy','Paste','PasteText','PasteFromWord','-','Undo','Redo' ] },
					{ name: 'basicstyles', items : [ 'Bold','Italic','Underline','Strike','Subscript','Superscript','-','RemoveFormat' ] },
					{ name: 'paragraph',   items : [ 'Blockquote','CreateDiv','-','JustifyLeft','JustifyCenter','JustifyRight','JustifyBlock'] },
					{ name: 'colors',      items : [ 'TextColor','BGColor' ] },
					{ name: 'insert',      items : [ 'Link', 'Image', 'addFile', 'addImage' ] },
					{ name: 'tools',       items : [ 'Maximize', 'About' ] },
					{ name: 'filemanager', items: ['filemanager'] }
				],
				simpleuploads_acceptedExtensions :'7z|avi|csv|doc|docx|flv|gif|gz|gzip|jpeg|jpg|mov|mp3|mp4|mpc|mpeg|mpg|ods|odt|pdf|png|ppt|pxd|rar|rtf|tar|tgz|txt|vsd|wav|wma|wmv|xls|xml|zip'

			}

			CKEDITOR.on('instanceReady', function(e) {

				var editor = e.editor;

				editor.on('simpleuploads.startUpload', function (e) {
					log("Uploading " + e.data.name + ", please wait...");
				})
				editor.on('simpleuploads.endUpload', function (e) {
					if (e.data.ok)
						log("File " + e.data.name + " uploaded correctly.");
					else
						log("Upload of " + e.data.name + " has failed.");
				})

				// the real listener
				editor.on( 'simpleuploads.finishedUpload' , function(ev) {

					var element = ev.data.element;
					// Add sample CSS class
					element.addClass("picture");

					// remove dimension attributes added in v 3.3.2
					element.removeAttribute("width");
					element.removeAttribute("height");
				});

			});

			CKEDITOR.on( 'instanceCreated', function( event ) {
				var editor = event.editor,
					element = editor.element;

				editor.config.filebrowserUploadUrl = uploadUrl; //+ '&Type=File';
				editor.config.filebrowserImageUploadUrl = uploadUrl;// + '&Type=Image';
				editor.config.customConfig = '';
			});

			CKEDITOR.replace( "editor1", config)

			function log(msg)
			{
				var p=document.createElement('p');
				p.appendChild( document.createTextNode( msg ) );
				document.getElementById('log').appendChild(p);
			}
			
			
	
			</script>
